func TestEncode(t *testing.T) {
	const structBin = `{
  "contract": {
    "name": "MyContract",
    "constructor": {
      "input": []
    }
  },
  "methods": [
    {
      "name": "add",
      "input": [
        {
          "type_id": 0
        }
      ],
      "output": [
        {
          "type_id": 0
        }
      ]
    },
    {
      "name": "make_student",
      "input": [
        {
          "type_id": 1
        },
        {
          "type_id": 6
        }
      ],
      "output": [
        {
          "type_id": 0
        }
      ]
    }
  ],
  "types": [
    {
      "id": 0,
      "type": "primitive",
      "primitive": "u64"
    },
    {
      "id": 1,
      "type": "struct",
      "fields": [
        {
          "type_id": 2
        },
        {
          "type_id": 3
        },
        {
          "type_id": 4
        }
      ]
    },
    {
      "id": 2,
      "type": "primitive",
      "primitive": "u32"
    },
    {
      "id": 3,
      "type": "primitive",
      "primitive": "str"
    },
    {
      "id": 4,
      "type": "vec",
      "fields": [
        {
          "type_id": 5
        }
      ]
    },
    {
      "id": 5,
      "type": "vec",
      "fields": [
        {
          "type_id": 3
        }
      ]
    },
    {
      "id": 6,
      "type": "struct",
      "fields": [
        {
          "type_id": 7
        }
      ]
    },
    {
      "id": 7,
      "type": "vec",
      "fields": [
        {
          "type_id": 8
        }
      ]
    },
    {
      "id": 8,
      "type": "array",
      "fields": [
        {
          "type_id": 1
        }
      ],
      "array_len": 10
    }
  ]
}`
	a, err := GenAbi(strings.NewReader(structBin))
	if err != nil {
		t.Error(err)
	}
	t.Run("encode", func(t *testing.T) {
		res, err := Encode(a, "make_student", []interface{}{
			uint32(1),
			"test",
			[]interface{}{
				[]interface{}{
					"hello",
					"world",
				},
			},
		}, []interface{}{
			[]interface{}{},
		})
		if err != nil {
			t.Error(err)
		}
		assert.Equal(t, "306d616b655f73747564656e7401000000107465737404081468656c6c6f14776f726c6400", common.Bytes2Hex(res))
	})
	t.Run("decode", func(t *testing.T) {
		l, err := a.DecodeInput("make_student", common.Hex2Bytes("306d616b655f73747564656e7401000000107465737404081468656c6c6f14776f726c6400"))
		assert.Nil(t, err)
		assert.Equal(t, uint32(1), l.Params[0].GetVal().([]scale.Compact)[0].GetVal())
	})
}

